bot = telebot.TeleBot('5284799411:AAFByfrB8QBcGqESGKOJ-bW5GXfDli__Xjc')
# –ö–æ–º–∞–Ω–¥–∞ start
@bot.message_handler(commands=["start"])
def start(m, res = False):
    
        # –î–æ–±–∞–≤–ª—è–µ–º –¥–≤–µ –∫–Ω–æ–ø–∫–∏
        markup=types.ReplyKeyboardMarkup(resize_keyboard=True)

        item1=types.KeyboardButton("–Ø —Å—Ç–∞–ª–∞ –∂–µ—Ä—Ç–≤–æ–π —Å–µ–∫—Å—É–∞–ª—å–Ω–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è")
        item2=types.KeyboardButton("–Ø —Å—Ç–∞–ª–∞ –∂–µ—Ä—Ç–≤–æ–π —Ñ–∏–∑–∏—á–µ—Å–∫–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è")
        item3=types.KeyboardButton("–Ø —Å—Ç–∞–ª–∞ –∂–µ—Ä—Ç–≤–æ–π –ø—Å–∏—Ö–æ–ª–æ–≥–∏—á–µ—Å–∫–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è")
        item4=types.KeyboardButton("–°–≤—è–∑–∞—Ç—å—Å—è —Å –Ω–∞–º–∏")
        item5=types.KeyboardButton("–ü–æ–ª—É—á–∏—Ç—å —Ä–µ–∫–≤–∏–∑–∏—Ç—ã –¥–ª—è –ø–æ–º–æ—â–∏ –ø–æ—Å—Ç—Ä–∞–¥–∞–≤—à–∏–º")
        markup.add(item1)
        markup.add(item2)
        markup.add(item3)
        markup.add(item4)
        markup.add(item5)
        bot.send_message(m.chat.id, '–ü—Ä–∏–≤–µ—Ç, –º–µ–Ω—è –∑–æ–≤—É—Ç –ê–ª–∏—èüòä! –Ø –±–æ—Ç —Ñ–æ–Ω–¥–∞ \n"–õ—É—á –∑–∞—â–∏—Ç—ã"‚ù§Ô∏è‚Äçüî• –∏ –º–æ–≥—É –æ—Ç–≤–µ—Ç–∏—Ç—å –Ω–∞ \n—Å–∞–º—ã–µ –ø–æ–ø—É–ª—è—Ä–Ω—ã–µ –≤–æ–ø—Ä–æ—Å—ã, –∫–æ—Ç–æ—Ä—ã–µ\n –∑–∞–¥–∞—é—Ç –ø–æ—Å—Ç—Ä–∞–¥–∞–≤—à–∏–µ –∏ —Å–≤–∏–¥–µ—Ç–µ–ª–∏ –Ω–∞—Å–∏–ª–∏—è.üîÜ \n \n–ö–∞–∫–∞—è —Å–∏—Ç—É–∞—Ü–∏—è —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏–∑—É–µ—Ç –≤–∞—à–µ –ø–æ–ª–æ–∂–µ–Ω–∏–µ?',  reply_markup=markup)
@bot.message_handler(content_types=["text"])
def handle_text(message):
    if message.text.strip() == '–Ø —Å—Ç–∞–ª–∞ –∂–µ—Ä—Ç–≤–æ–π —Å–µ–∫—Å—É–∞–ª—å–Ω–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è':
            mess = f'–û–±—Ä–∞—Ç–∏—Ç–µ—Å—å –∫–∞–∫ –º–æ–∂–Ω–æ —Å–∫–æ—Ä–µ–µ –≤ —Ç–µ—á–µ–Ω–∏–µ 7 –¥–Ω–µ–π –≤ –æ–¥–∏–Ω –∏–∑ —Ü–µ–Ω—Ç—Ä–æ–≤ –ø–æ–º–æ—â–∏ –∂–µ—Ä—Ç–≤–∞–º —Å–µ–∫—Å—É–∞–ª—å–Ω–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è, –∫–æ—Ç–æ—Ä—ã–µ –∫—Ä—É–≥–ª–æ—Å—É—Ç–æ—á–Ω–æ —Ä–∞–±–æ—Ç–∞—é—Ç –≤ –ë–∏—à–∫–µ–∫–µ, –ö–∞–Ω—Ç–µ –∏ –¢–æ–∫–º–∞–∫–µ. –ü–æ–º–æ—â—å –±–µ—Å–ø–ª–∞—Ç–Ω–∞—è. –ù–µ –º–æ–π—Å—è, –Ω–∏—á–µ–≥–æ –Ω–µ –µ—à—å –∏ –Ω–µ –ø–µ–π. –¢—É –æ–¥–µ–∂–¥—É, –∫–æ—Ç–æ—Ä–∞—è –±—ã–ª–∞ –Ω–∞ —Ç–µ–±–µ –≤ –º–æ–º–µ–Ω—Ç —Å–ª—É—á–∏–≤—à–µ–≥–æ—Å—è, –ø–æ–ª–æ–∂–∏ –≤ –±—É–º–∞–∂–Ω—ã–π –ø–∞–∫–µ—Ç –∏ –ø—Ä–∏–Ω–µ—Å–∏ —Å —Å–æ–±–æ–π. –ï—Å–ª–∏ —Å –º–æ–º–µ–Ω—Ç–∞ —Å–æ–≤–µ—Ä—à–µ–Ω–∏—è —Å–µ–∫—Å—É–∞–ª—å–Ω–æ–≥–æ –ø—Ä–µ—Å—Ç—É–ø–ª–µ–Ω–∏—è –ø—Ä–æ—à–ª–æ –±–æ–ª–µ–µ 7 –¥–Ω–µ–π, –Ω—É–∂–Ω–æ –æ–±—Ä–∞—Ç–∏—Ç—å—Å—è –≤ –ø–æ–ª–∏—Ü–∏—é. –ï—Å–ª–∏ —Å–æ—Å—Ç–æ—è–Ω–∏–µ —Ç–≤–æ–µ–≥–æ –∑–¥–æ—Ä–æ–≤—å—è –ø–ª–æ—Ö–æ–µ –∏–ª–∏ —É—Ö—É–¥—à–∞–µ—Ç—Å—è, –Ω—É–∂–Ω–æ –æ–±—Ä–∞—Ç–∏—Ç—å—Å—è –∫ —Å–µ–º–µ–π–Ω–æ–º—É –≤—Ä–∞—á—É –∏–ª–∏ –≤ –æ—Ç–¥–µ–ª–µ–Ω–∏–µ –≠–∫—Å—Ç—Ä–µ–Ω–Ω–æ–π –º–µ–¥–∏—Ü–∏–Ω—ã. –ï—Å–ª–∏ —Å –º–æ–º–µ–Ω—Ç–∞ —Å–æ–≤–µ—Ä—à–µ–Ω–∏—è —Å–µ–∫—Å—É–∞–ª—å–Ω–æ–≥–æ –ø—Ä–µ—Å—Ç—É–ø–ª–µ–Ω–∏—è –ø—Ä–æ—à–ª–æ –±–æ–ª–µ–µ 7 –¥–Ω–µ–π, —Ç–æ –≤–∑—è—Ç—å –º–µ–¥–∏—Ü–∏–Ω—Å–∫–∏–π –º–∞—Ç–µ—Ä–∏–∞–ª –¥–ª—è –¥–æ–∫–∞–∑–∞—Ç–µ–ª—å—Å—Ç–≤ —É–∂–µ –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ, –Ω–æ, –≤–æ–∑–º–æ–∂–Ω–æ, —Ç–µ–±–µ –±—É–¥–µ—Ç –Ω—É–∂–Ω–∞ –ø—Å–∏—Ö–æ–ª–æ–≥–∏—á–µ—Å–∫–∞—è –ø–æ–º–æ—â—å –∏–ª–∏ –ø–æ–º–æ—â—å –≤—Ä–∞—á–∞ (–≥–∏–Ω–µ–∫–æ–ª–æ–≥–∞, –¥–µ—Ä–º–∞—Ç–æ–≤–µ–Ω–µ—Ä–æ–ª–æ–≥–∞) –¥–ª—è –ø—Ä–æ–≤–µ–¥–µ–Ω–∏—è –∞–Ω–∞–ª–∏–∑–æ–≤ –Ω–∞ –∏–Ω—Ñ–µ–∫—Ü–∏–∏, –ø–µ—Ä–µ–¥–∞—é—â–∏–µ—Å—è –ø–æ–ª–æ–≤—ã–º –ø—É—Ç–µ–º. –î–ª—è —ç—Ç–æ–≥–æ –Ω—É–∂–Ω–æ –±—É–¥–µ—Ç –æ–±—Ä–∞—Ç–∏—Ç—å—Å—è –Ω–∞ –ø—Ä–∏–µ–º –∫ –ø—Å–∏—Ö–æ–ª–æ–≥—É –∏–ª–∏ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–µ–º—É –≤—Ä–∞—á—É. \n\n <b>–ü–æ—Å—Ç—Ä–∞–¥–∞–≤—à–∏–π —á–µ–ª–æ–≤–µ–∫ –Ω–∏–∫–æ–≥–¥–∞ –Ω–µ –≤–∏–Ω–æ–≤–∞—Ç –≤ —Å–µ–∫—Å—É–∞–ª—å–Ω–æ–º –Ω–∞—Å–∏–ª–∏–∏!</b>'
            bot.send_message(message.chat.id, mess, parse_mode='html')
    elif message.text.strip() == '–°–≤—è–∑–∞—Ç—å—Å—è —Å –Ω–∞–º–∏':
            mess = f'–ï—Å–ª–∏ —É –≤–∞—Å –µ—Å—Ç—å –≤–æ–ø—Ä–æ—Å—ã –æ–±—Ä–∞—â–∞–π—Ç–µ—Å—å –ø–æ –≥–æ—Ä—è—á–µ–π –ª–∏–Ω–∏–∏: <b>1234</b> \n –ù–∞—à–∞ –ø–æ—á—Ç–∞: \n noviolence@gmail.com'
            bot.send_message(message.chat.id, mess, parse_mode='html')
            
    elif message.text.strip() == '–Ø —Å—Ç–∞–ª–∞ –∂–µ—Ä—Ç–≤–æ–π —Ñ–∏–∑–∏—á–µ—Å–∫–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è':
            mess = f'<a href="https://katsaylidi.ru/article/chto-delat-esli-izbili-cheloveka">–ß—Ç–æ –¥–µ–ª–∞—Ç—å –µ—Å–ª–∏ —è –ø–æ–¥–≤–µ—Ä–≥–ª–∞—Å—å —Ñ–∏–∑–∏—á–µ—Å–∫–æ–º—É –Ω–∞—Å–∏–ª–∏—é?</a>'
            bot.send_message(message.chat.id, mess, parse_mode='html')
    
    elif message.text.strip() == '–Ø —Å—Ç–∞–ª–∞ –∂–µ—Ä—Ç–≤–æ–π –ø—Å–∏—Ö–æ–ª–æ–≥–∏—á–µ—Å–∫–æ–≥–æ –Ω–∞—Å–∏–ª–∏—è':
            mess = f'–û–±—Ä–∞—Ç–∏—Ç–µ—Å—å –ø–æ –≥–æ—Ä—è—á–µ–π –ª–∏–Ω–∏–∏, 1234 –∏–ª–∏ –Ω–∞–ø–∏—à–∏—Ç–µ –Ω–∞–º –Ω–∞ –ø–æ—á—Ç—É noviolence@gmail.com –∏ –ø—Ä–∏—Å–æ–µ–¥–∏–Ω—è—Ç–µ—Å—å –≤ –Ω–∞—à–µ–º—É –ê–ù–û–ù–ò–ú–ù–û–ú–£ —Ñ–æ—Ä—É–º—É –≥–¥–µ –≤—ã —Å–º–æ–∂–µ—Ç–µ –ø–æ–¥–µ–ª–∏—Ç—å—Å—è —Å–≤–æ–µ–π –∏—Å—Ç–æ—Ä–∏–µ–π –∏ –ø–æ–ª—É—á–∏—Ç—å —Å–æ–≤–µ—Ç—ã –æ—Ç —ç–∫—Å–ø–µ—Ä—Ç–æ–≤.'
            bot.send_message(message.chat.id, mess, parse_mode='html')
    
    elif message.text.strip() == '–ü–æ–ª—É—á–∏—Ç—å —Ä–µ–∫–≤–∏–∑–∏—Ç—ã –¥–¥—è –ø–æ–º–æ—â–∏ –ø–æ—Å—Ç—Ä–∞–¥–∞–≤—à–∏–º':
            mess = f'<b>–ù–∞—à–∏ —Ä–µ–∫–≤–µ–∑–∏—Ç—ã: </b> \n Optima bank: 2348 7384 7556, \n –≠–ª—Å–æ–º: +996 555 105 353, \n Qiwi: +996 564 239 323, \n Payeer: P1067879164, \n PayPal: 9485 4549 3840' 
            bot.send_message(message.chat.id, mess, parse_mode='html')
    
bot.polling(none_stop=True, interval=0)
